            PrestamoId = prestamo.PrestamoId,
            Valor = prestamo.Balance

        });

    }

    private void Buscar()
    {
        if (mora.MoraId > 0)
        {
            var encontrado = MorasBLL.Buscar(mora.MoraId);

            if (encontrado != null)
                this.mora = encontrado;
            else
                Toast.ShowWarning("No encontrado");
        }
    }

    public void Guardar()
    {
        bool guardo;

